export const getApi = async () => {
    const URL = 'https://dog.ceo/api/breeds/image/random';
    try {
        const response = await fetch(URL);
        const data = await response.json();
        return data;

    }
    catch (error) {
        console.log(`Algo deu errado ${error}`);
    }
}
